import React from "react";
import ReactTable from "react-table";

import { authenticationService } from "../_services";
import { addToCart } from "../_helpers";
import "react-table/react-table.css";

class ShopPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            currentUser: authenticationService.currentUserValue,
            data: null
        };
    }

    componentDidMount() {
        let newState = Object.assign({}, this.state);
        newState.data = [
            {
                id_: 1,
                name: 'alma',
                price: 100,
                description: 'This is an appleasddasdssadappleasddasdssadappleasddasdssadappleasddasdssadappleasddasdssadappleasddasdssadappleasddasdssadappleasddasdssadappleasddasdssad'
            },
            {
                id_: 2,
                name: 'banana',
                price: 200,
                description: 'This is a banana'
            },
        ];
        this.setState(newState);
        //userService.getAll().then(users => this.setState({ users }));
    }

    render() {
        const { data, currentUSer} = this.state;
        console.log(data);
        return (
            <div>
                { data && (
                 <ReactTable
                    data={data}

                    filterable
                    defaultFilterMethod={(filter, row) =>
                        String(row[filter.id]).includes(filter.value)}
                    getTdProps={(state, rowInfo, column, instance) => {
                        return {
                        onClick: (e, handleOriginal) => {
                            console.log("A Td Element was clicked!");
                            console.log("It was in this row:", rowInfo);

                            // IMPORTANT! React-Table uses onClick internally to trigger
                            // events like expanding SubComponents and pivots.
                            // By default a custom 'onClick' handler will override this functionality.
                            // If you want to fire the original onClick handler, call the
                            // 'handleOriginal' function.
                            if (handleOriginal) {
                                handleOriginal();
                            }
                        }
                        };
                    }}
                    columns={[
                        {
                            Header: "Shop items",
                            columns: [
                                {
                                    Header: "#",
                                    accessor: "id_",
                                    width: 50,
                                    filterable: false,
                                },
                                {
                                    Header: "Name",
                                    accessor: "name",
                                    maxWidth: 100
                                },
                                {
                                    Header: "Short description",
                                    accessor: "description",
                                    width: 300,
                                    maxWidth: 500
                                },
                                {
                                    Header: "Price",
                                    accessor: "price",
                                    width: 100
                                },
                                {
                                    Header: "Add to cart",
                                    accessor: "add",
                                    Cell: <button onClick={addToCart(rowInfo, currentUSer)}>Add to cart</button>
                                },
                            ]
                        }
                    ]}
                    defaultPageSize={10}
                    className="-striped -highlight"
                />
                )}
            </div>
        );
    }
}

export { ShopPage };
